import React from "react"
import Header from "./components/Header"
import Footer from "./components/Footer"
import Items from "./components/Items"
import Categories from "./components/Categories"
import ShowFullItem from "./components/ShowFullItem"


class Rpp extends React.Component {
    constructor(props) {
        super(props) 
            this.state = {
                orders: [],
                currentItems: [],
                items: [
                    {
                    id: 1,
                    title: 'Vinul Syrah',
                    img: 'scau.jpg',
                    desc: 'Syrah este un soi de struguri care produce vinuri roșii bogate și complexe, din regiunea Hawkes Bay ',
                    category: 'chairs',
                    price: '49.99,'
                    },
                    {
                        id: 2,
                    title: 'Saltea',
                    img: 'Saltea.jpg',
                    desc: 'Salteaua este un element central pentru calitatea somnului',
                    category: 'sofa',
                    price: '79.99,'
                    },
                    {
                        id: 3,
                    title: 'Masa',
                    img: 'mas.jpg',
                    desc: 'Masa este o piesă esențială de mobilier',
                    category: 'tables',
                    price: '149.99,'
                    },
                    {
                        id: 4,
                    title: 'Dulap',
                    img: 'dula.jpeg',
                    desc: 'Dulapul oferă spațiu de depozitare esențial pentru haine și alte obiecte personal',
                    category: 'light',
                    price: '249.99,'
                    },
                    {
                        id: 5,
                    title: 'Noptiera',
                    img: 'noptiera.webp',
                    desc: 'Noptiera, plasată în mod convenabil lângă pat, poate servi drept suport pentru lampă de noptiere',
                    category: 'chairs',
                    price: '39.99,'
                    }
                    
                ],
                showFullItem: false,
                fullItem: {}
            }
            this.state.currentItems = this.state.items
            this.addToOrder = this.addToOrder.bind(this)
            this.deleteOrder = this.deleteOrder.bind(this)
            this.chooseCategory = this.chooseCategory.bind(this)
            this.onShowItem = this.onShowItem.bind(this)
        }
    
    render () {
return (
    <div className="wrapper">
        <Header orders={this.state.orders} onDelete={this.deleteOrder} />
        <Categories chooseCategory={this.chooseCategory} />
        <Items onShowItem={this.onShowItem} items={this.state.currentItems} onAdd={this.addToOrder}  />
        {this.state.showFullItem && <ShowFullItem onAdd={this.addToOrder} item={this.state.fullItem} items={this.state.fullItem}  />}
        <Footer />
    </div>
)
}
onShowItem(item) {
    this.setState({fullItem: item})
    this.setState({showFullItem: !this.state.showFullItem})
}

chooseCategory(category) {
    if(category === 'all') {
        this.setState({currentItems: this.state.items})
        return 
    }
     this.setState({
         currentItems: this.state.items.filter(el => el.category === category)
     })
}

deleteOrder(id){
    this.setState({ orders: this.state.orders.filter(el => el.id !== id)})

}
addToOrder(item) {
    let isInArray = false
    this.state.orders.forEach(el => {
        if(el.id === item.id)
        isInArray = true
    })
    if(!isInArray)
    this.setState({orders: [...this.state.orders, item] })
}

}


export default Rpp
