import React from "react"
import Header from "./components/Header"
import Footer from "./components/Footer"
import Items from "./components/Items"
import Categores from "./components/Categores"

class Rpp extends React.Component {
    constructor(props) {
        super(props) 
            this.state = {
                orders: [],
                currentItems: [],
                items: [
                    {
                    id: 1,
                    title: 'Scaun',
                    img: 'scau.jpg',
                    desc: 'Scaunul poate fi o piesă versatilă, fie că este folosit în timpul mesei',
                    category: 'chairs',
                    price: '49.99,'
                    },
                    {
                        id: 2,
                    title: 'Saltea',
                    img: 'Saltea.jpg',
                    desc: 'Salteaua este un element central pentru calitatea somnului',
                    category: 'tables',
                    price: '79.99,'
                    },
                    {
                        id: 3,
                    title: 'Masa',
                    img: 'mas.jpg',
                    desc: 'Masa este o piesă esențială de mobilier',
                    category: 'sofa',
                    price: '149.99,'
                    },
                    {
                        id: 4,
                    title: 'Dulap',
                    img: 'dula.jpeg',
                    desc: 'Dulapul oferă spațiu de depozitare esențial pentru haine și alte obiecte personal',
                    category: 'light',
                    price: '249.99,'
                    },
                    {
                        id: 5,
                    title: 'Noptiera',
                    img: 'noptiera.webp',
                    desc: 'Noptiera, plasată în mod convenabil lângă pat, poate servi drept suport pentru lampă de noptiere',
                    category: 'chairs',
                    price: '39.99,'
                    }
                    
                ]
            }
            this.state.currentItems = this.state.items
            this.addToOrder = this.addToOrder.bind(this)
            this.deleteOrder = this.deleteOrder.bind(this)
            this.chooseCategory = this.chooseCategory.bind(this)
        }
    
    render () {
return (
    <div className="wrapper">
        <Header orders={this.state.orders} onDelete={this.deleteOrder} />
        <Categores chooseCategory={this.chooseCategory} />
        <Items  items={this.state.items} onAdd={this.addToOrder}  />
        <Footer />
    </div>
)
}

chooseCategory(category) {
    this.setState({
        currentItems: this.setState.items.filter(el => el.category === category)
    })
}

deleteOrder(id){
    this.setState({ orders: this.state.orders.filter(el => el.id !== id)})

}
addToOrder(item) {
    let isInArray = false
    this.state.orders.forEach(el => {
        if(el.id === item.id)
        isInArray = true
    })
    if(!isInArray)
    this.setState({orders: [...this.state.orders, item] })
}

}


export default Rpp
